import React, { useState } from 'react';
import PostThread from './PostThread.js';
import '../App.css';

export default function PostList({ posts, user, onVote, onVoteReply, onReply, onDelete }) {
  const [openPostId, setOpenPostId] = useState(null);

  return (
    <div>
      {posts.map(p => {
        const scoreClass = p.score >= 0 ? 'post-score positive' : 'post-score negative';
        return (
          <div key={p.id} className="post">
            <h3 onClick={() => setOpenPostId(openPostId===p.id?null:p.id)}>{p.title}</h3>
            <p className="post-author">Autor: {p.user}</p>
            <p className={scoreClass}>{p.score}</p>

            <button className="btn btn-like" onClick={() => onVote(p.id, 'up')}>Lubię</button>
            <button className="btn btn-dislike" onClick={() => onVote(p.id, 'down')}>Nie lubię</button>
            {(user==='Admin' || user===p.user) && (
              <button className="btn btn-delete" onClick={() => onDelete(p.id)}>Usuń</button>
            )}

            {openPostId===p.id && (
              <PostThread post={p} user={user} onVoteReply={onVoteReply} onReply={onReply}/>
            )}
          </div>
        );
      })}
    </div>
  );
}
