import React, { useState } from 'react';
import '../App.css';

export default function PostThread({ post, user, onVoteReply, onReply }) {
  const [replyText, setReplyText]         = useState('');
  const [activeReplyTo, setActiveReplyTo] = useState(null);

  // helper rekurencyjny: jedną odpowiedź przerabiamy na komponent z własnym stanem open/closed
  const ReplyItem = ({ r }) => {
    const [open, setOpen] = useState(true);
    const children = post.replies.filter(cr => cr.parentId === r.id);

    // wybór klasy na podstawie score:
    let cls = 'reply-score ';
    if (r.score > 0)      cls += 'positive';
    else if (r.score < 0) cls += 'negative';
    else                   cls += 'neutral';

    return (
      <div className="reply" style={{ marginLeft: r.parentId ? '1.5rem' : 0 }}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          {children.length > 0 && (
            <button
              className="toggle-btn"
              onClick={() => setOpen(o => !o)}
              aria-label={open ? 'Zwiń wątek' : 'Rozwiń wątek'}
            >
              {open ? '⤴️' : '⤵️'}
            </button>
          )}
          <strong>{r.user}:</strong>&nbsp;{r.text}
        </div>
        <div style={{ margin: '0.25rem 0 0.75rem 1.5rem', display: open ? 'block' : 'none' }}>
          <button className="btn-like" onClick={() => onVoteReply(post.id, r.id, 'up')}>
            Lubię
          </button>
          <button className="btn-dislike" onClick={() => onVoteReply(post.id, r.id, 'down')}>
            Nie lubię
          </button>
          <span className={cls} style={{ margin: '0 1rem' }}>
            {r.score}
          </span>
          <button className="btn" onClick={() => setActiveReplyTo(r.id)}>
            Odpowiedz
          </button>

          {activeReplyTo === r.id && (
            <div style={{ marginTop: '.5rem' }}>
              <textarea
                placeholder="Twoja odpowiedź..."
                value={replyText}
                onChange={e => setReplyText(e.target.value)}
                style={{ width: '100%', minHeight: '3rem' }}
              />
              <button className="btn" onClick={() => {
                if (!replyText) return;
                onReply(post.id, r.id, replyText);
                setReplyText('');
                setActiveReplyTo(null);
              }}>
                Wyślij
              </button>
            </div>
          )}

          {/* rekurencyjnie dzieci */}
          {open && children.map(cr => <ReplyItem key={cr.id} r={cr} />)}
        </div>
      </div>
    );
  };

  // tylko top-level replies
  const topReplies = post.replies.filter(r => r.parentId == null);

  return (
    <div className="post-thread">
      <h4>Odpowiedzi ({post.replies.length})</h4>

      {topReplies.map(r => <ReplyItem key={r.id} r={r} />)}

      {/* formularz na nową, top-level odpowiedź */}
      <div className="reply" style={{ marginTop: '1rem' }}>
        <textarea
          placeholder="Twoja odpowiedź..."
          value={replyText}
          onChange={e => setReplyText(e.target.value)}
          style={{ width: '100%', minHeight: '3rem' }}
        />
        <button className="btn" onClick={() => {
          if (!replyText) return;
          onReply(post.id, null, replyText);
          setReplyText('');
        }}>
          Odpowiedz
        </button>
      </div>
    </div>
  );
}
