import React, { useState } from 'react';

function Reply({ reply, user, onVoteReply, onReply }) {
  // collapsed === true means the body + children are hidden
  const [collapsed, setCollapsed] = useState(false);
  const hasChildren = reply.replies && reply.replies.length > 0;

  return (
    <div className="reply">
      <div className="reply-header">
        {hasChildren && (
          <button
            className="toggle-btn"
            onClick={() => setCollapsed(!collapsed)}
          >
            {collapsed ? 'ðŸ”½' : 'ðŸ”¼'}
          </button>
        )}
        <span className="reply-author">{reply.user}</span>
      </div>

      {!collapsed && (
        <>
          <p className="reply-text">{reply.text}</p>

          <div className="reply-actions">
            <button onClick={() => onVoteReply(reply.id, 'up')}>LubiÄ™</button>
            <button onClick={() => onVoteReply(reply.id, 'down')}>Nie lubiÄ™</button>
            <button onClick={() => onReply(reply.id)}>Odpowiedz</button>
          </div>

          {hasChildren && (
            <div className="nested-replies">
              {reply.replies.map(child => (
                <Reply
                  key={child.id}
                  reply={child}
                  user={user}
                  onVoteReply={onVoteReply}
                  onReply={onReply}
                />
              ))}
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default function PostThread({ post, user, onVoteReply, onReply }) {
  return (
    <div className="thread">
      {post.replies.map(r => (
        <Reply
          key={r.id}
          reply={r}
          user={user}
          onVoteReply={onVoteReply}
          onReply={onReply}
        />
      ))}

      <form
        className="reply-form"
        onSubmit={e => {
          e.preventDefault();
          const txt = e.target.elements.replyText.value.trim();
          if (txt) {
            onReply(post.id, txt);
            e.target.reset();
          }
        }}
      >
        <textarea name="replyText" placeholder="Twoja odpowiedÅº..." />
        <button type="submit">Odpowiedz</button>
      </form>
    </div>
  );
}
