import React, { useState } from 'react';
import '../App.css';

const PostThread = ({ posts, onLike, onDislike, onDelete }) => {
  return (
    <div>
      {posts.map(post => (
        <ThreadItem
          key={post.id}
          post={post}
          onLike={onLike}
          onDislike={onDislike}
          onDelete={onDelete}
        />
      ))}
    </div>
  );
};

const ThreadItem = ({ post, onLike, onDislike, onDelete }) => {
  const [open, setOpen] = useState(true);
  const hasReplies = Array.isArray(post.replies) && post.replies.length > 0;

  // wybór klasy na podstawie wyniku
  let scoreClass = 'post-score ';
  if (post.score > 0) scoreClass += 'positive';
  else if (post.score < 0) scoreClass += 'negative';
  else scoreClass += 'neutral';

  return (
    <div className="post">
      <div className="post-header">
        {hasReplies && (
          <button
            className="toggle-btn"
            onClick={() => setOpen(o => !o)}
            aria-label={open ? 'Zwiń wątek' : 'Rozwiń wątek'}
          >
            {open ? '⤴️' : '⤵️'}
          </button>
        )}
        <h3>{post.title}</h3>
      </div>
      <div className="post-author">Autor: {post.author}</div>
      <div className="post-content">{post.content}</div>
      <div className="post-footer">
        <span className={scoreClass}>{post.score}</span>
        <div className="post-actions">
          <button
            className={`btn-like ${post.liked ? 'active' : ''}`}
            onClick={() => onLike(post.id)}
          >
            Lubię
          </button>
          <button
            className={`btn-dislike ${post.disliked ? 'active' : ''}`}
            onClick={() => onDislike(post.id)}
          >
            Nie lubię
          </button>
          <button className="btn-delete" onClick={() => onDelete(post.id)}>
            Usuń
          </button>
        </div>
      </div>

      {hasReplies && open && (
        <div className="nested-replies">
          {post.replies.map(reply => (
            <ThreadItem
              key={reply.id}
              post={reply}
              onLike={onLike}
              onDislike={onDislike}
              onDelete={onDelete}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default PostThread;
