import React, { useState } from 'react';
import PostThread from './PostThread.js';
import '../App.css';
export default function PostList({ posts, user, onVote, onVoteReply, onReply, onDelete }) {
  const [openPostId, setOpenPostId] = useState(null);

  return (
    <div>
      {posts.map(p => (
        <div key={p.id} className="post">
          {/* 1. Nagłówek */}
          <h3
            className="post-header"
            onClick={() => setOpenPostId(openPostId === p.id ? null : p.id)}
          >
            {p.title}
          </h3>

          {/* 2. Autor */}
          <p className="post-author">Autor: {p.user}</p>

          {/* 3. Treść artykułu */}
          <p className="post-content">{p.text}</p>

          {/* 4. Kreska */}
          <hr/>

          {/* 5. Liczba polubień i akcje */}
          <div className="post-footer">
            <span>Polubień: {p.score}</span>
            <div className="post-actions">
              <button onClick={() => onVote(p.id, 'up')}>Lubię</button>
              <button onClick={() => onVote(p.id, 'down')}>Nie lubię</button>
              <button onClick={() => onDelete(p.id)}>Usuń</button>
            </div>
          </div>

          {/* Rozwinięcie wątków */}
          {openPostId === p.id && (
            <PostThread
              post={p}
              user={user}
              onVoteReply={onVoteReply}
              onReply={onReply}
            />
          )}
        </div>
      ))}
    </div>
  );
}
