import React, { useState } from 'react';
import '../App.css';

export default function PostThread({ post, user, onVoteReply, onReply }) {
  const [replyText, setReplyText]     = useState('');
  const [activeReplyTo, setActiveReplyTo] = useState(null);

  const topReplies   = post.replies.filter(r => r.parentId == null);
  const childReplies = id => post.replies.filter(r => r.parentId === id);

  const handleSubmit = parentId => {
    if (!replyText) return;
    onReply(post.id, parentId, replyText);
    setReplyText('');
    setActiveReplyTo(null);
  };

  const renderReply = r => (
    <div key={r.id} className="reply">
      <p><strong>{r.user}:</strong> {r.text}</p>
      <div style={{ margin: '0.5rem 0' }}>
        <button className="btn-like" onClick={() => onVoteReply(post.id, r.id, 'up')}>Lubię</button>
        <button className="btn-dislike" onClick={() => onVoteReply(post.id, r.id, 'down')}>Nie lubię</button>
        <span style={{ margin: '0 1rem' }} className={r.score >= 0 ? 'positive' : 'negative'}>
          {r.score}
        </span>
        <button className="btn" onClick={() => setActiveReplyTo(r.id)}>Odpowiedz</button>
      </div>

      {activeReplyTo === r.id && (
        <div>
          <textarea
            placeholder="Twoja odpowiedź..."
            value={replyText}
            onChange={e => setReplyText(e.target.value)}
          />
          <button className="btn" onClick={() => handleSubmit(r.id)}>Wyślij</button>
        </div>
      )}

      {childReplies(r.id).map(cr => renderReply(cr))}
    </div>
  );

  return (
    <div>
      <h4>Odpowiedzi ({post.replies.length})</h4>
      {topReplies.map(r => renderReply(r))}
      <div className="reply">
        <textarea
          placeholder="Twoja odpowiedź..."
          value={replyText}
          onChange={e => setReplyText(e.target.value)}
        />
        <button className="btn" onClick={() => handleSubmit(null)}>Odpowiedz</button>
      </div>
    </div>
  );
}
