import { useState, useEffect } from 'react';

export default function PostThread({ post, user }) {
  const [replies, setReplies] = useState([]);
  const [text, setText]       = useState('');

  useEffect(() => {
    fetch(`/posts/${post.id}/replies`)
      .then(r => r.json())
      .then(setReplies);
  }, [post.id]);

  const submitReply = () => {
    fetch('/reply', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        postId:   post.id,
        parentId: null,
        text,
        user
      })
    })
    .then(() => {
      setText('');
      return fetch(`/posts/${post.id}/replies`);
    })
    .then(r => r.json())
    .then(setReplies);
  };

  return (
    <div style={{
      marginTop: '0.5rem',
      paddingLeft: '1rem',
      borderLeft: '2px solid #ccc'
    }}>
      <h4>Odpowiedzi ({replies.length})</h4>
      {replies.map(r => (
        <div key={r.id} style={{ marginBottom: '0.5rem' }}>
          <strong>{r.user}:</strong> {r.text}
        </div>
      ))}

      <textarea
        placeholder="Twoja odpowiedź…"
        value={text}
        onChange={e => setText(e.target.value)}
      />
      <br/>
      <button onClick={submitReply}>Wyślij odpowiedź</button>
    </div>
  );
}
