import React, { useState } from 'react';
import PostThread from './PostThread.js';

export default function PostList({ posts, user, onVote, onReply }) {
  const [openId, setOpenId] = useState(null);

  return (
    <div>
      {posts.map(p => (
        <div key={p.id} style={{ border: '1px solid #ccc', margin: '0.5rem 0', padding: '0.5rem' }}>
          <h3 style={{ cursor: 'pointer' }} onClick={() => setOpenId(openId === p.id ? null : p.id)}>
            {p.title} (âš¡ {p.score})
          </h3>
          <p>{p.text}</p>
          <button onClick={() => onVote(p.id, 'up')}>Upvote</button>
          <button onClick={() => onVote(p.id, 'down')}>Downvote</button>
          {openId === p.id && (
            <PostThread post={p} user={user} onReply={onReply} />
          )}
        </div>
      ))}
    </div>
  );
}
