import React, { useState } from 'react';

export default function PostThread({ post, user, onReply }) {
  const [text, setText] = useState('');

  const submit = () => {
    if (!text) return;
    onReply(post.id, null, text);
    setText('');
  };

  return (
    <div style={{ paddingLeft: '1rem', borderLeft: '3px solid #ddd', marginTop: '0.5rem' }}>
      <h4>Odpowiedzi ({post.replies.length})</h4>
      {post.replies.map(r => (
        <div key={r.id} style={{ marginBottom: '0.25rem' }}>
          <strong>{r.user}:</strong> {r.text}
        </div>
      ))}
      <textarea
        placeholder="Twoja odpowiedÅº..."
        value={text}
        onChange={e => setText(e.target.value)}
      />
      <button onClick={submit}>Odpowiedz</button>
    </div>
  );
}
