import { useState, useEffect, useCallback } from 'react';
import Login from './components/Login.js';
import NewPostForm from './components/NewPostForm.js';
import PostList from './components/PostList.js';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState(() => {
    try { return JSON.parse(localStorage.getItem(STORAGE_KEY)) || []; }
    catch { return []; }
  });


  useEffect(() => {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(posts));
  }, [posts]);

  const addPost = ({ title, text }) => {
    const newPost = {
      id: Date.now(),
      user,
      title,
      text,
      score: 0,
      upvotes: [],
      downvotes: [],
      replies: []
    };
    setPosts(prev => [newPost, ...prev]);
  };

  const vote = (postId, type) => {
    setPosts(prev => prev.map(p => {
      if (p.id !== postId) return p;
      // usuń poprzednie głosy
      const up = p.upvotes.filter(u => u !== user);
      const down = p.downvotes.filter(u => u !== user);
      if (type === 'up') up.push(user);
      if (type === 'down') down.push(user);
      return {
        ...p,
        upvotes: up,
        downvotes: down,
        score: up.length - down.length
      };
    }));
  };

  const addReply = (postId, parentId, text) => {
    setPosts(prev => prev.map(p => {
      if (p.id !== postId) return p;
      const reply = { id: Date.now(), user, text, parentId };
      return { ...p, replies: [...p.replies, reply] };
    }));
  };

  if (!user) return <Login onLogin={setUser} />;

  return (
    <div className="container">
      <h1>Witaj, {user}</h1>
      <NewPostForm onPost={addPost} />
      <PostList
        posts={posts}
        user={user}
        onVote={vote}
        onReply={addReply}
      />
    </div>
  );
}

export default App;
